// 2. AUTOSPLIT (dzielimy na zdania)
// ===============================
function autosplit(raw) {
    let clean = raw.trim().replace(/\s+/g, " ");

    // Dzielenie po .?!
    let parts = clean.split(/(?<=[.?!])\s+/);

    let corpus = [];

    for (let p of parts) {
        p = p.trim();
        if (p.length < 5) continue;
        if (!p.endsWith(".")) p += ".";
        corpus.push(p);
    }
    return corpus;
}

let CORPUS = autosplit(RAW_CORPUS);

