function normalize(t) {
    t = t.toLowerCase();

    const repl = {
        "Ä…":"a","Ä‡":"c","Ä™":"e","Å‚":"l","Å„":"n",
        "Ã³":"o","Å›":"s","Å¼":"z","Åº":"z"
    };

    for (let a in repl) {
        t = t.replace(new RegExp(a, "g"), repl[a]);
    }

    t = t.replace(/[^a-z0-9 ]/g, " ");
    return t.trim();
}

