// ===============================
function best_line_for(word) {
    let nword = normalize(word);

    let best = null;
    let best_pos = Infinity;

    for (let line of CORPUS) {
        let nline = normalize(line);
        let pos = nline.indexOf(nword);

        if (pos !== -1 && pos < best_pos) {
            best = line;
            best_pos = pos;
        }
    }
    return best;
}

