function trim_to_two_sentences(text) {
    let parts = text.split(".");
    parts = parts.map(p => p.trim()).filter(p => p.length > 0);

    if (parts.length >= 2)
        return parts[0] + ". " + parts[1] + ".";

    return parts[0] + ".";
}

