function answer(message) {
    let n = normalize(message);

    if (!n) return "Napisz coÅ› wiÄ™cej ğŸ™‚";

    let words = n.split(" ");
    let key = words[words.length - 1];  // ostatnie sÅ‚owo

    let line = best_line_for(key);
    if (!line) return `Nie mam jeszcze informacji o sÅ‚owie: '${key}'.`;

    return trim_to_two_sentences(line);
}

